import React, { useState } from 'react';

import Grid from '@mui/material/Grid';
import { Alert, Button, CircularProgress, Container, TextField, Typography } from '@mui/material';
import login1 from '../../../images/log3.jpeg'
import logImg from '../../../images/log4.jpeg'
import MuiButton from '../../../styledComponent/MuiButton';
import { Link, useLocation, useHistory } from 'react-router-dom';
import useAuth from '../../../hooks/useAuth';



const Login = () => {
    const {user, loginUser} =useAuth();
    const [loginData,setLoginData,isLoading,authError ] =useState({})
    const location = useLocation();
    const history = useHistory()
    const style ={

        minHeight:'600',
        height:'100vh',
        backgroundPosition:'center',
        backgroundSize:'cover',
        backgroundRepeat:'no-repeat',
        background: `url(${login1})`,



    }
    const handleOnChange =(e)=>{
        const field = e.target.name;
        const value = e.target.value;
        const newLoginData = {...loginData};
        newLoginData[field] =value;
        setLoginData(newLoginData);
        console.log(field,value);
    }
    const handleLoginSubmit =(e)=>{
        loginUser(loginData.email,loginData.password,location,history)
        alert('submit');
        e.preventDefault();
    }
    return (
        <div >
            <Container sx={{ backgroundColor:'#f2f2f2',width:'50%',my:'3'}}>
                <Grid container spacing={2}>

                    <Grid item xs={12} md={12}>
                        <Typography variant="h4" gutterBottom >Login</Typography>
                        <form onSubmit={handleLoginSubmit} >
                            <TextField id="standard-basic"
                                sx={{ width: '50%', m: '1', px: '5' }}
                                label="Your email"
                                type="email"
                                name="email"
                                onBlur={handleOnChange}
                                variant="standard" />
                             <br />
                             <br />
                            <TextField
                                sx={{ width: '50%', m: '1' }}
                                id="outlined-password-input"
                                label="Your Password"
                                type="password"
                                name= "password"
                                onBlur={handleOnChange}
                                autoComplete="current-password"
                            />
                            <br />
                            <br />
                            <Link to ='/register'>
                                <Button variant="text"> New user? please register</Button>

                            </Link>
                            <br />

                            <MuiButton sx={{ width:'25%'}} type="submit"> login</MuiButton>


                        </form>
                        {
                            isLoading && <CircularProgress color="success" />
                        }
                        {
                            user?.email && <Alert severity="success">account created successfully!</Alert>
                        }
                        {
                            authError && <Alert severity="error">{authError}</Alert>
                        }

                    </Grid>


                </Grid>
            </Container>
        </div>
    );
};

export default Login;
