                    <div className="col-sm-10">
                        <input onBlur={handlePasswordChange} type="password" className="form-control" id="inputPassword3"/>
                    </div>
                </div>

                <div className="row mb-3">
                    <div className="col-sm-10 offset-sm-2">
                        <div className="form-check">
                            <input className="form-check-input" type="checkbox" id="gridCheck1"/>
                            <label class ="form-check-label" for="gridCheck1">
                            Example checkbox
                            </label>
                        </div>
                    </div>
                </div>
                <button  type="submit" className="btn btn-primary">Sign in</button>
            </form> */}
            <Container>
                <Grid container spacing={2}>

                    <Grid item xs={12} md={12}>
                        <Typography variant="h4" gutterBottom >Register </Typography>
                        <form onSubmit={handleLoginSubmit} >
                            <TextField 
                                id="standard-basic"
                                sx={{ width: '75%', m: '1', px: '5' }}
                                label="Your email"
                                name="email"
                                type="email"

                                onChange={handleOnChange}
                                variant="standard" />
                            <br />
                            <br />
                            <TextField
                                sx={{ width: '75%', m: '1' }}
                                id="standard-basic"
                                label="Your Password"
                                type="password"
                                name="password"
                                onChange={handleOnChange}
                                autoComplete="current-password"
                                variant="standard"
                            />

                            <TextField
                                sx={{ width: '75%', margin: '30px' }}
                                id="standard-basic"
                                label="Retype Password"
                                type="password"
                                name="password2"
                                onChange={handleOnChange}
                                variant="standard"
                            />
                            <br />
                            <br />
                            <Link to='/login'>
                                <Button variant="text"> Already register?please Register</Button>

                            </Link>
                            <br />


                            <MuiButton sx={{ width: '25%' ,marginLeft:'3'}} type="submit"> login</MuiButton>
                            <MuiButton onClick={handleSignInWithGoogle}> Google sign IN</MuiButton>





                        </form>

                    </Grid>


                </Grid>
            </Container>
        </div>
    );
};

export default Register;
