import React, { useEffect, useState } from 'react';
import useAuth from '../../../hooks/useAuth';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import MuiButton from '../../../styledComponent/MuiButton';
import { Alert } from '@mui/material';
import { useForm } from "react-hook-form";

function createData(name, calories, fat, carbs, protein) {
    return { name, calories, fat, carbs, protein };
}

const ManageAllProducts = () => {
    const [order,setOrder] = useState([]);
    const [id,setId] =useState('');
    const { register, handleSubmit } = useForm();

    useEffect(()=>{
        fetch('http://localhost:5000/allorders')
        .then(res =>res.json())
        .then(data=>setOrder(data))
    },[])

    const onSubmit = data => {
        console.log(data,id);
        fetch(`http://localhost:5000/statusUpdate/${id}`,{
            method:'PUT',
            headers:{"content-type":"application/json"},
            body:JSON.stringify(data)
        })
        .then(res =>res.json())
        .then(result=>console.log(result));
    };
   //handle status update 
    const handleStatusUpdate =(id)=>{
       setId(id);


    }
//handle status update
const handleDeleteOrder =(id)=>{
    const proceed = window.confirm("Do you want to delete!")
    if(proceed){
        const url = `http://localhost:5000/orders/${id}`
        fetch(url, {
            method: 'DELETE'


        })
            .then(res => res.json())
            .then(data => {
                if (data.deletedCount > 0) {
                    <Alert severity="success">account created successfully!</Alert>
                    alert('deleted successfully');
                    const remainOrder = order.filter(ord => ord._id !== id)
                    setOrder(remainOrder);

                }
            })
    }
}




    return (
        <div>
            <h2>manage all order Total:  {order.length}</h2>
            <TableContainer component={Paper}>
                <Table sx={{ minWidth: 650 }} aria-label="simple table">
                    <TableHead>
                        <TableRow>
                            <TableCell>Customer Name:</TableCell>
                            <TableCell align="right">Email:</TableCell>
                            <TableCell align="right">Product Name</TableCell>
                            <TableCell align="right">product price</TableCell>
                            <TableCell align="right">Phone Number</TableCell>
                            <TableCell align="right">actionn</TableCell>
                            <TableCell align="right">statuss</TableCell>
                            <TableCell align="right">status manage</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {order.map((row) => (
                            <TableRow
                                key={row._id}
                                sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                            >
                                <TableCell component="th" scope="row">
                                    {row.customerName}
                                </TableCell>
                                <TableCell align="right">{row.email}</TableCell>
                                <TableCell align="right">{row.productName}</TableCell>
                                <TableCell align="right">{row.productPrice}</TableCell>
                                <TableCell align="right">{row.phone}</TableCell>
                                <TableCell align="right">{row?.status}</TableCell>
                                <TableCell align="right"><MuiButton onClick={() => handleDeleteOrder(row._id)}>Delete</MuiButton></TableCell>

                                <TableCell align="right">{
                                    <form onSubmit={handleSubmit(onSubmit)}>
                                        <label>{row?.status}</label>
                                        <select onClick={() => handleStatusUpdate(row._id)} {...register ("status")}>
                                            <option value={row?.status}>{row?.status}</option>
                                            <option value="approved">approved</option>
                                            <option value="done">Done</option>
                                        </select>
                                        <input type="submit" />
                                    </form>
                                }</TableCell>

                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>

        </div>
    );
};

export default ManageAllProducts;
