import './ReviewHome.css'
import Rating from '@mui/material/Rating';
import Stack from '@mui/material/Stack';


import CardContent from '@mui/material/CardContent';


import Typography from '@mui/material/Typography';
import React, { useEffect, useState } from 'react';
import { Container, ratingClasses } from '@mui/material';
import { Box } from '@mui/system';

const Reviewhome = () => {
    const [reviews,setReviews]= useState([]);
    useEffect(()=>{
        fetch('http://localhost:5000/allreview')
        .then(res =>res.json())
        .then(data =>setReviews(data))
    },[])
    return (
        <div>
            <Box sx={{ flexGrow: 1 }}>
                <Container>
                    <Typography className='text-primary my-5' sx={{ fontWeight: "600" }} variant="h3" component="div">
                        Our Customer review {reviews.length}
                    </Typography>
                    <Grid container spacing={{ xs: 2, md: 3 }} columns={{ xs: 3, sm: 8, md: 12 }}>
                        {
                            reviews.map(review => <Grid item xs={3} sm={4} md={4} >
                                <Card sx={{ minWidth: 275, boxShadow: 3 }}>
                                    <CardContent>



                                        <Typography sx={{ color: 'Info.main' }} variant="h5" component="div">
                                            {review.email}
                                        </Typography>
                                        <Typography sx={{ mb: 1.5 }} color="text.secondary">
                                            ${review.comments}
                                        </Typography>
                                        <Typography variant="body2">
                                            {/* <Rating
                                                initialRating={review.rating}
                                                emptySymbol="far fa-star icon-color"
                                                fullSymbol="fas fa-star icon-color"
                                                readonly></Rating>

                                            <br /> */}
                                            <Rating name="half-rating-read" defaultValue={review.rating} precision={0.5} readOnly />

                                        </Typography>
                                    </CardContent>

                                </Card>

                            </Grid>)

                        }








                    </Grid>
                </Container>
            </Box>
        </div>
