import { useEffect, useState } from "react";
import initializeFirebase from "../Pages/Login/Login/Firebase/firebase.init";
import { getAuth, createUserWithEmailAndPassword, signOut, onAuthStateChanged, signInWithEmailAndPassword, GoogleAuthProvider, signInWithPopup} from "firebase/auth";



//initialize firebase app
initializeFirebase();

const useFirebase =()=>{
    const [user,setUser] =useState({});
    const [isLoadeing,setIsLoading] = useState(true);
    const auth = getAuth();
    const googleProvider = new GoogleAuthProvider();

    //sign in with google 
    const signInWithGoogle = () => {

        return signInWithPopup(auth, googleProvider)

    }

//register with email and password 
    const registerUser =(email,password)=>{
        createUserWithEmailAndPassword(auth,email,password)
        .then((userCredential) => {
            // Signed in 
            const user = userCredential.user;
            // ...
        })
            .catch((error) => {
                const errorCode = error.code;
                const errorMessage = error.message;
                // ..
            });
    }
    // signed in user 
    const loginUser = (email,password)=>{
        signInWithEmailAndPassword(auth, email, password)
            .then((userCredential) => {
                // Signed in 
                const user = userCredential.user;
                // ...
            })
            .catch((error) => {
                const errorCode = error.code;
                const errorMessage = error.message;
            });


    }
    //observe the user 
    useEffect(()=>{
        const unSubscribe = onAuthStateChanged(auth, (user) => {
            if (user) {
                setUser(user)

            } else {
                setUser({});

            }
        });
        return ()=>unSubscribe;

    },[])

    //log out
    const logOut =()=>{

        signOut(auth).then(() => {
            // Sign-out successful.
        }).catch((error) => {
            // An error happened.
        });

    }

    return {
        user,
        registerUser,
        logOut,
        loginUser,
        signInWithGoogle,setUser
    }

}

export default useFirebase;
