import React from 'react';
import Grid from '@mui/material/Grid';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import MuiButton from '../../../styledComponent/MuiButton';


const Product = (props) => {
    const { name, description, img,price} = props.item;
    const [placingOpen, setPlacingOpen] = React.useState(false);
    const handlePlacingOpen = () => setPlacingOpen(true);
    const handlePlacingClose = () => setPlacingOpen(false);
    return (

        <Grid item xs={3} sm={4} md={4} >
            <Card sx={{ minWidth: 275 }}>
                <CardContent>

                    <CardMedia
                        component="img"
                        height="140"
                        image={img}
                        alt="green iguana"
                    />

                    <Typography variant="h5" component="div">
                        {name}
                    </Typography>
                    <Typography sx={{ mb: 1.5 }} color="text.secondary">
                        ${price}
                    </Typography>
                    <Typography variant="body2">
                        {description}
                        <br />

                    </Typography>
                </CardContent>
                <CardActions>
                    <MuiButton onClick={handlePlacingOpen} sx={{margin:'auto'}}> order Now</MuiButton>
                </CardActions>
            </Card>

        </Grid>
    );
};

export default Product;
